import React, { Component } from 'react'

export class PlaceChoiceFields extends Component {

  render() {
    this.roomNumbers = []
    for (let i = 1; i <= 10; i++) {
      this.roomNumbers.push(i);
    }
    this.roomNumbers = this.roomNumbers.map((floorNum) => <option value={floorNum}>{floorNum}</option>);
    return (
      <div>
        <select name="tower">
          <option value="A">A</option>
          <option value="B">B</option>
        </select>

        <select name="floor">
          {this.roomNumbers}
        </select>

        <select name="tower">
          <option value="A">A</option>
          <option value="B">B</option>
        </select>
      </div>
    )
  }
}

export default PlaceChoiceFields
