import React, { Component } from 'react'

export class PlaceChoiceFields extends Component {

  render() {
    const rowStyle = {display: 'flex'}
    this.roomNumbers = []
    this.floors = []
    for (let i = 1; i <= 10; i++) {
      this.roomNumbers.push(<option value={i}>{i}</option>);
    }
    for (let i = 3; i <= 27; i++) {
      this.floors.push(<option value={i}>{i}</option>);
    }
    return (
      <div style={rowStyle}>
        <div>
          <label>Башня</label>
        <select name="tower">
          <option value="A">A</option>
          <option value="B">B</option>
        </select>
        </div>

        <div>
        <label>Этаж</label>
        <select name="floor">
          {this.roomNumbers}
        </select>
        </div>

        <div>
        <label>Комната</label>
        <select name="tower">
          {this.floors}
        </select>
        </div>
      </div>
    )
  }
}

export default PlaceChoiceFields
