import React, { Component } from 'react'

export class SendAndClear extends Component {

    render() {
        const buttonRowStyle = { backgroundColor: 'red', display: 'flex', flexDirection: 'row', width: '20%' }
        const bookButtonStyle = { display: 'flex', justifySelf: 'stretch', backgroundColor: 'black', flex: '1' };
        const clearButtonStyle = { backgroundColor: 'green', flexGrow: '4' };
        return (
            <div style={buttonRowStyle}>
                <div style={bookButtonStyle}><input type='submit' value='Забронировать' /></div>
                <div style={clearButtonStyle}><input type='submit' value='Очистить' /> </div>
            </div>
        )
    }
}

export default SendAndClear
