import React, { Component } from 'react'

export class DateField extends Component {
    render() {
        const mainStyle = { display: 'flex', flexDirection: 'row', width: '100%', height: '100%', gap: '30px' }
        const containerStyle = { display: 'flex', flexDirection: 'column', alignItems: 'flex-start', width: '100%', height: '100%' }
        const selectorStyle = { width: '100%', height: '100%', fontFamily: "'Exo 2', sans-serif", fontWeight: '500', fontSize: '18px', backgroundColor: '#F4F4F9', border: 'none', marginTop: '4px' }
        var durations = [];
        for (let i = 0; i <= 3; i++) {
            for (let j of ['00', '15', '30', '45']) {
                durations.push(<option value={i + ':' + j} key={i + ':' + j}></option>)
            }
        }
        return (
            <div style={mainStyle}>
                <div style={containerStyle}>
                    <label style={{ textAlign: 'left' }}>Дата</label>
                    <input style={{ width: '100%', height: '100%', fontFamily: "'Exo 2', sans-serif", fontWeight: '500', fontSize: '18px', backgroundColor: '#F4F4F9', border: 'none', marginTop: '4px' }} type="date" id="bookDate" name="bookDate" />
                </div>
                <div style={containerStyle}>
                    <label style={{ textAlign: 'left' }}>Продолжительность</label>
                    <select name="floor" style={selectorStyle} id="floor">
                        {durations}
                    </select>


                </div>
            </div>

        )
    }
}

export default DateField
