import React, { Component } from 'react'

export class SendAndClear extends Component {

    render() {
        //this.state.opacity = 1;
        // this.setState(() => { return { opacity: 1 } });
        const buttonRowStyle = { display: 'flex', height: '100%', flexDirection: 'row', width: '100%' }
        const bookButtonStyle = { flex: '4', marginRight: '20px', fontFamily: "'Exo 2', sans-serif", fontWeight: '600', fontSize: '22px', color: '#F4F4F9', backgroundColor: '#586F7C', border: 'none' };
        bookButtonStyle.opacity = this.opacity;
        const clearButtonStyle = { flex: '1', fontFamily: "'Exo 2', sans-serif", fontWeight: '600', fontSize: '22px', color: '#F4F4F9', backgroundColor: '#8AA0AC', border: 'none' };
        // onMouseDown={this.setState(() => { return { opacity: 0 } })}
        return (
            <div style={buttonRowStyle}>
                <input style={bookButtonStyle} type='button' value='Забронировать' onMouseDown={() => { this.setState(() => { return { opacity: 0 } }) }} />
                <input style={clearButtonStyle} type='button' value='Очистить' />
            </div>
        )
    }
}

export default SendAndClear
