import React, { Component } from 'react'

export class SendAndClear extends Component {

    render() {
        const buttonRowStyle = { backgroundColor: 'red', display: 'flex', flexDirection: 'row' }
        const bookButtonStyle = { display: 'flex', backgroundColor: 'black', flex: '1' };
        const clearButtonStyle = { backgroundColor: 'green', flex: '1' };
        return (
            <div style={buttonRowStyle}>
                <input style={bookButtonStyle} type='submit' value='Забронировать' />
                <input style={clearButtonStyle} type='submit' value='Очистить' />
            </div>
        )
    }
}

export default SendAndClear
