import React, { Component } from 'react'

export class PlaceChoiceFields extends Component {

  render() {
    this.roomNumbers = []
    this.floors = []
    for (let i = 1; i <= 10; i++) {
      this.roomNumbers.push(<option value={i}>{i}</option>);
    }
    for (let i = 3; i <= 27; i++) {
      this.floors.push(<option value={i}>{i}</option>);
    }
    // this.roomNumbers = this.roomNumbers.map((floorNum) => );
    //  this.roomNumbers = this.roomNumbers.map((floorNum) => );
    return (
      <div>
        <select name="tower">
          <option value="A">A</option>
          <option value="B">B</option>
        </select>

        <select name="floor">
          {this.roomNumbers}
        </select>

        <select name="tower">
          {this.floors}
        </select>
      </div>
    )
  }
}

export default PlaceChoiceFields
