import React, { Component } from 'react'

export class SendAndClear extends Component {
    constructor(props) {
        super(props);
        this.state = { visibility: 'visible' };
    }
    submitData() {
        console.log(document.getElementById('tower').value)
        console.log(document.getElementById('floor').value)
        console.log(document.getElementById('room').value)
        console.log(document.getElementById('bookDate').value)
        console.log(document.getElementById('additionalInformation').value)
    }
    clearData() {
        document.getElementById('tower').value = 'A';
        document.getElementById('floor').value = '3';
        document.getElementById('room').value = '1';
        document.getElementById('bookDate').value = ''
        document.getElementById('additionalInformation').value = '';
    }
    render() {
        const buttonRowStyle = { display: 'flex', height: '100%', flexDirection: 'row', width: '100%' }
        var bookButtonStyle = { flex: '4', marginRight: '20px', fontFamily: "'Exo 2', sans-serif", fontWeight: '600', fontSize: '22px', color: '#F4F4F9', backgroundColor: '#586F7C', border: 'none' };
        bookButtonStyle.visibility = this.state.visibility;
        const clearButtonStyle = { flex: '1', fontFamily: "'Exo 2', sans-serif", fontWeight: '600', fontSize: '22px', color: '#F4F4F9', backgroundColor: '#8AA0AC', border: 'none' };
        return (
            <div style={buttonRowStyle}>
                <input style={bookButtonStyle} type='button' value='Забронировать' onMouseDown={this.submitData.bind(this)} />
                <input style={clearButtonStyle} type='button' value='Очистить' onMouseDown={this.clearData.bind(this)} />
            </div>
        )
    }
}

export default SendAndClear
